!(({ languages, hooks }) => {
  (languages.etlua = {
    delimiter: { pattern: /^<%[-=]?|-?%>$/, alias: "punctuation" },
    "language-lua": { pattern: /[\s\S]+/, inside: languages.lua },
  }),
    hooks.add("before-tokenize", (a) => {
      languages["markup-templating"].buildPlaceholders(
        a,
        "etlua",
        /<%[\s\S]+?%>/g
      );
    }),
    hooks.add("after-tokenize", (a) => {
      languages["markup-templating"].tokenizePlaceholders(a, "etlua");
    });
})(Prism);
