hljs.registerLanguage("bnf", (() => {
  "use strict";
  return (
    {C_LINE_COMMENT_MODE, C_BLOCK_COMMENT_MODE, APOS_STRING_MODE, QUOTE_STRING_MODE}
  ) => {
    return {
      name: "Backus\u2013Naur Form",
      contains: [
        {
          className: "attribute",
          begin: /</,
          end: />/,
        },
        {
          begin: /::=/,
          end: /$/,
          contains: [
            {
              begin: /</,
              end: />/,
            },
            C_LINE_COMMENT_MODE,
            C_BLOCK_COMMENT_MODE,
            APOS_STRING_MODE,
            QUOTE_STRING_MODE,
          ],
        },
      ],
    };
  };
})());
