hljs.registerLanguage(
  "cpp",
  (() => {
    "use strict";
    return (e) => {
      const i = e.requireLanguage("c-like").rawDefinition();
      return (
        (i.disableAutodetect = !1),
        (i.name = "C++"),
        (i.aliases = ["cc", "c++", "h++", "hpp", "hh", "hxx", "cxx"]),
        i
      );
    };
  })()
);
