hljs.registerLanguage("python-repl", (() => {
  "use strict";
  return n => {
    return {
      aliases: ["pycon"],
      contains: [
        {
          className: "meta",
          starts: {
            end: / |$/,
            starts: {
              end: "$",
              subLanguage: "python",
            },
          },
          variants: [
            { begin: /^>>>(?=[ ]|$)/ },
            {
              begin: /^\.\.\.(?=[ ]|$)/,
            },
          ],
        },
      ],
    };
  };
})());
