hljs.registerLanguage("shell", (() => {
  "use strict";
  return s => {
    return {
      name: "Shell Session",
      aliases: ["console"],
      contains: [
        {
          className: "meta",
          begin: "^\\s{0,3}[/\\w\\d\\[\\]()@-]*[>%$#]",
          starts: { end: "$", subLanguage: "bash" },
        },
      ],
    };
  };
})());
