hljs.registerLanguage("tap", (() => {
  "use strict";
  return ({HASH_COMMENT_MODE}) => {
    return {
      name: "Test Anything Protocol",
      case_insensitive: !0,
      contains: [
        HASH_COMMENT_MODE,
        {
          className: "meta",
          variants: [
            {
              begin: "^TAP version (\\d+)$",
            },
            { begin: "^1\\.\\.(\\d+)$" },
          ],
        },
        {
          begin: "(s+)?---$",
          end: "\\.\\.\\.$",
          subLanguage: "yaml",
          relevance: 0,
        },
        { className: "number", begin: " (\\d+) " },
        {
          className: "symbol",
          variants: [{ begin: "^ok" }, { begin: "^not ok" }],
        },
      ],
    };
  };
})());
