hljs.registerLanguage(
  "vbscript-html",
  (() => {
    "use strict";
    return (n) => {
      return {
        name: "VBScript in HTML",
        subLanguage: "xml",
        contains: [
          {
            begin: "<%",
            end: "%>",
            subLanguage: "vbscript",
          },
        ],
      };
    };
  })()
);
